import React from "react";
import Axios from "axios";
import {Navigate, useParams} from "react-router-dom";
import Navbar from "./Navbar";
import AppLoader from "./AppLoader";

class Picture extends React.Component {
    constructor() {
        super();
        this.state = {
            title: "", description: "", picture: {}, redirect: false, errors: []
        };
    }

    componentDidMount() {
        if (localStorage.getItem('token')) {
            let id = this.props.params.id;
            let headers = {
                headers: {
                    'API-TOKEN': localStorage.getItem('token')
                }
            }
            Axios.get(`http://127.0.0.1:8000/api/picture/${id}`, headers)
                .then(res => {
                    console.log(res.data);
                    this.setState({picture: res.data})
                })
                .catch(error => {
                    console.log(error.response);
                })
        } else {
            this.setState({redirect: true})
        }
    };

    render() {
        if (this.state.redirect) {
            return (<Navigate to="/"/>);
        }
        return (<>
                <Navbar/>
                <div className="container my-5">
                    {this.state.picture && this.state.picture.user ? <div className="row">
                        <div className="col-8">
                            <img className="img-fluid"
                                 src={`http://127.0.0.1:8000/storage/pictures/${this.state.picture.image}`}/>
                        </div>
                        <div className="col-4">
                            <div className="author">
                                <h3>{this.state.picture.user.name}</h3>
                                <AppLoader/>
                            </div>
                        </div>
                    </div> : <AppLoader/>}
                </div>
            </>);
    }
}

export default (props) => (<Picture
        {...props}
        params={useParams()}/>)
